mapper.select:
SELECT combinedData.nno, combinedData.tno, combinedData.title, combinedData.content, combinedData.writer, combinedData.registDate, combinedData.updateDate, combinedData.viewCnt, combinedData.replyCnt, combinedData.fileName, combinedData.type FROM ( SELECT n.nno, NULL AS tno, n.title, n.content, n.writer, n.registDate, n.updateDate, n.viewCnt, NULL AS replyCnt, CONCAT(tni.uuid, '_', tni.filename) AS fileName, 'notice' AS type FROM tbl_notice n LEFT OUTER JOIN tbl_notice_img tni ON n.nno = tni.nno AND tni.ord = 0 WHERE n.nno > 0 UNION ALL SELECT NULL AS nno, b.tno, b.title, b.content, b.writer, b.registDate, b.updateDate, b.viewCnt, b.replyCnt, CONCAT(tbi.uuid, '_', tbi.filename) AS fileName, 'board' AS type FROM tbl_board b LEFT OUTER JOIN tbl_board_img tbi ON b.tno = tbi.tno AND tbi.ord = 0 WHERE b.tno > 0
b.title LIKE CONCAT('%', #{keyword}, '%')
b.content LIKE CONCAT('%', #{keyword}, '%')
b.writer LIKE CONCAT('%', #{keyword}, '%')
) AS combinedData ORDER BY combinedData.nno DESC, combinedData.tno DESC LIMIT #{skip}, #{size}

mapper.update:
UPDATE tbl_board
SET title = #{title}, content = #{content}, writer = #{writer}, updateDate = now()
WHERE tno = #{tno}

UPDATE tbl_board
SET viewCnt = viewCnt +1
WHERE tno = #{tno}
