mapper.update:
UPDATE tbl_reply
SET gno = #{gno}
WHERE rno = #{rno}

mapper.insert:
UPDATE tbl_board
SET replyCnt = replyCnt + 1
WHERE tno = #{tno}

mapper.select:
SELECT IF(rno = gno, 0,1 ) AS STEP,
tno, rno, gno, reply, replyer, replyDate, modifyDate
FROM tbl_reply
WHERE tno = #{tno}
ORDER BY gno ASC, rno ASC 
LIMIT #{page.skip}, #{page.size}
